import React from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { fetchCollectionRequestAsync } from '../../redux/shop/shop.actions';
import { selectIsCollectionFetching, selectIsCollectionLoaded } from '../../redux/shop/shop.selectors';

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';
import WithSpinner from '../../components/with-spinner/with-spinner.component';



const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

  componentDidMount() {
    const { fetchCollectionRequestAsync } = this.props;
    fetchCollectionRequestAsync();
  }

  render() {
    const { match, isFetching, isCollectionLoaded } = this.props;
    return (
      <div className='shop-page'>
        <Route exact path={`${match.path}`}
          render={(props) => <CollectionsOverviewWithSpinner isLoading={!isCollectionLoaded} {...props} />
          } />
        <Route path={`${match.path}/:collectionId`}
          render={(props) => <CollectionPageWithSpinner isLoading={isFetching} {...props} />} />
      </div>
    )
  };
}

const mapStateToProps = createStructuredSelector({
  isFetching: selectIsCollectionFetching,
  isCollectionLoaded: selectIsCollectionLoaded
});

const mapDispatchToProps = dispatch => ({
  fetchCollectionRequestAsync: () => dispatch(fetchCollectionRequestAsync())
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
