import { createSelector } from 'reselect';

const selectShop = state => state.shop;

const COLLECTION_ID = {
    hats: 1,
    sneakers: 2,
    jackets: 3,
    women: 4,
    mens: 5
}

export const selectColletions = createSelector(
    [selectShop],
    shop => shop.collections
);

export const selectCollection = (collectionUrlParams) => createSelector(
    [selectColletions],
    collections => collections.find(collection =>
        collection.id === COLLECTION_ID[collectionUrlParams])
);
